import React, { useState } from 'react';

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [displayText, setDisplayText] = useState(''); // State for displayed text

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
    setDisplayText(event.target.value); // Update displayed text
  };

  return (
    <div className="App">
      <h1 style={{ textAlign: 'center', color: '#007bff' }}>Search Bar</h1> {/* Centered heading with blue color */}
      <style jsx>
        {`
          .search-wrapper {
            display: flex;
            justify-content: center;
            margin: 20px auto;
          }

          .search-input {
            display: block;
            width: 100%;
            padding: 10px;
            border: 1px solid #ccc;
            border-radius: 5px;
            font-size: 16px;
            box-sizing: border-box;
            background-color: #f5f5f5;
            color: #333;
          }

          .displayed-text {
            text-align: center;
            font-size: 36px; /* Large font size */
            color: red; /* Red color */
            margin-top: 10px; /* Add some space between search bar and text */
          }
        `}
      </style>
      <div className="search-wrapper">
        <input
          type="text"
          placeholder="Search..."
          value={searchTerm}
          onChange={handleSearchChange}
          className="search-input"
        />
      </div>
      <div className="displayed-text">{displayText}</div> {/* Display the search term */}
    </div>
  );
}

export default App;
