import React, { useState } from 'react';

function App() {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  return (
    <div className="App">
      <h1 style={{ textAlign: 'center' }}>Search Bar</h1> {/* Center the heading */}
      <style jsx>
        {`
          .search-wrapper {
            display: flex; /* Allow centering with justify-content */
            justify-content: center; /* Horizontally center the search bar */
            margin: 20px auto; /* Add some margin for spacing */
          }

          .search-input {
            display: block;
            width: 100%; /* Make it fill the available space within the wrapper */
            padding: 10px;
            border: 1px solid #ccc; /* Add a border */
            border-radius: 5px; /* Add rounded corners */
            font-size: 16px; /* Adjust font size */
            box-sizing: border-box; /* Ensure padding doesn't shrink content area */
            background-color: #f5f5f5; /* Add a light background color */
            color: #333; /* Change the text color to a darker shade */
          }
        `}
      </style>
      <div className="search-wrapper"> {/* Wrap the input in a container */}
        <input
          type="text"
          placeholder="Search..."
          value={searchTerm}
          onChange={handleSearchChange}
          className="search-input"
        />
      </div>
    </div>
  );
}

export default App;
